    private void botonCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonCalcularActionPerformed
        try {
            double d = Double.parseDouble(cajaDiametro.getText());
            double h = Double.parseDouble(cajaAltura.getText());
            double p = Double.parseDouble(cajaPeso.getText());
            double s = Double.parseDouble(cajaSeguridad.getText());
            double e = Double.parseDouble(cajaEspesor.getText());

            double admisible = (double) (p * h * d * s) / (double) (2 * e);

            textoResultado.setText(admisible + "");

            double limite = Double.parseDouble(cajaLimite.getText());
            String color = "rojo";




            if (admisible == limite) { // amarrillo
                imagen1.setIcon(new javax.swing.ImageIcon(getClass().getResource("/a3d/amarillo.png"))); // NOI18N
                color = "amarillo";
            } else {
                if (admisible > limite) {
                    imagen1.setIcon(new javax.swing.ImageIcon(getClass().getResource("/a3d/rojo.png"))); // NOI18N
                    color = "rojo";
                } else {
                    imagen1.setIcon(new javax.swing.ImageIcon(getClass().getResource("/a3d/verde.png"))); // NOI18N
                    color = "verde";
                }
            }

            A.stop();
            A = new Animacion(color, imagenAnimacion);
            A.start();
        } catch (Exception ex) {
            JOptionPane.showMessageDialog(this, "Falta introducir alg√∫n dato");
        }
    }//GEN-LAST:event_botonCalcularActionPerformed

